SELECT partsupp.ps_comment, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost, AVG(partsupp.ps_suppkey) AS avg_partsupp_ps_suppkey, MIN(partsupp.ps_availqty) AS min_partsupp_ps_availqty, AVG(partsupp.ps_partkey) AS avg_partsupp_ps_partkey FROM partsupp WHERE ((partsupp.ps_partkey = 569) OR (partsupp.ps_partkey = 40)) OR (partsupp.ps_partkey BETWEEN 236 AND 568) GROUP BY partsupp.ps_comment HAVING COUNT(*) > 1574 AND AVG(partsupp.ps_partkey) > 21317 ORDER BY partsupp.ps_comment