SELECT partsupp.ps_comment, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost, SUM(partsupp.ps_availqty) AS sum_partsupp_ps_availqty, MIN(partsupp.ps_supplycost) AS min_partsupp_ps_supplycost, COUNT(partsupp.ps_supplycost) AS count_partsupp_ps_supplycost FROM partsupp WHERE partsupp.ps_suppkey < 490 AND partsupp.ps_suppkey > 214 AND partsupp.ps_suppkey > 619 GROUP BY partsupp.ps_comment HAVING COUNT(*) > 7165 AND COUNT(partsupp.ps_supplycost) > 62383 ORDER BY partsupp.ps_comment