SELECT orders.o_orderstatus, AVG(orders.o_shippriority) AS avg_orders_o_shippriority, MAX(orders.o_custkey) AS max_orders_o_custkey, STDDEV(orders.o_totalprice) AS stddev_orders_o_totalprice, SUM(orders.o_totalprice) AS sum_orders_o_totalprice FROM orders WHERE (orders.o_orderstatus LIKE '%C') OR (orders.o_orderstatus LIKE 'A%') AND orders.o_comment LIKE 'A%' GROUP BY orders.o_orderstatus ORDER BY orders.o_orderstatus