SELECT lineitem.l_shipmode, VARIANCE(lineitem.l_discount) AS variance_lineitem_l_discount, STDDEV(lineitem.l_suppkey) AS stddev_lineitem_l_suppkey, MIN(lineitem.l_linenumber) AS min_lineitem_l_linenumber, MIN(lineitem.l_linenumber) AS min_lineitem_l_linenumber, SUM(lineitem.l_extendedprice) AS sum_lineitem_l_extendedprice FROM lineitem WHERE (lineitem.l_receiptdate >= '2001-10-06') OR (lineitem.l_shipinstruct LIKE '%B%') GROUP BY lineitem.l_shipmode ORDER BY lineitem.l_shipmode