SELECT lineitem.l_shipdate, MIN(lineitem.l_partkey) AS min_lineitem_l_partkey, STDDEV(lineitem.l_linenumber) AS stddev_lineitem_l_linenumber, MIN(lineitem.l_extendedprice) AS min_lineitem_l_extendedprice, SUM(lineitem.l_linenumber) AS sum_lineitem_l_linenumber, AVG(lineitem.l_discount) AS avg_lineitem_l_discount FROM lineitem WHERE lineitem.l_commitdate >= '2018-03-24' AND lineitem.l_linenumber BETWEEN 313 AND 612 AND lineitem.l_suppkey = 588 GROUP BY lineitem.l_shipdate ORDER BY lineitem.l_shipdate