SELECT nation.n_comment, MAX(nation.n_nationkey) AS max_nation_n_nationkey, MIN(nation.n_regionkey) AS min_nation_n_regionkey FROM nation WHERE ((nation.n_comment LIKE 'A%' AND nation.n_nationkey < 748) OR (nation.n_name = 'FGTWI')) OR (nation.n_regionkey BETWEEN 771 AND 888) GROUP BY nation.n_comment HAVING COUNT(*) > 2865 AND MIN(nation.n_regionkey) > 34798 ORDER BY nation.n_comment