SELECT partsupp.ps_comment, AVG(partsupp.ps_supplycost) AS avg_partsupp_ps_supplycost, STDDEV(partsupp.ps_supplycost) AS stddev_partsupp_ps_supplycost, STDDEV(partsupp.ps_suppkey) AS stddev_partsupp_ps_suppkey FROM partsupp WHERE partsupp.ps_partkey BETWEEN 78 AND 387 AND partsupp.ps_availqty = 662 AND partsupp.ps_partkey < 210 GROUP BY partsupp.ps_comment HAVING COUNT(*) > 762 AND AVG(partsupp.ps_supplycost) > 35831 ORDER BY partsupp.ps_comment