SELECT supplier.s_name, supplier.s_phone, SUM(supplier.s_suppkey) AS sum_supplier_s_suppkey, STDDEV(supplier.s_acctbal) AS stddev_supplier_s_acctbal, MAX(supplier.s_nationkey) AS max_supplier_s_nationkey, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey FROM supplier WHERE supplier.s_address = 'BPNVH' AND supplier.s_nationkey BETWEEN 230 AND 655 GROUP BY supplier.s_name, supplier.s_phone ORDER BY supplier.s_phone, supplier.s_name