SELECT customer.c_address, customer.c_comment, customer.c_mktsegment, VARIANCE(customer.c_custkey) AS variance_customer_c_custkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, MAX(customer.c_custkey) AS max_customer_c_custkey FROM customer WHERE customer.c_address LIKE '%C' AND customer.c_name = 'HLOLV' AND customer.c_comment = 'JEXKK' GROUP BY customer.c_address, customer.c_comment, customer.c_mktsegment ORDER BY customer.c_mktsegment, customer.c_comment