SELECT orders.o_clerk, MIN(orders.o_shippriority) AS min_orders_o_shippriority, STDDEV(orders.o_orderkey) AS stddev_orders_o_orderkey, MAX(orders.o_totalprice) AS max_orders_o_totalprice, AVG(orders.o_orderkey) AS avg_orders_o_orderkey, STDDEV(orders.o_orderkey) AS stddev_orders_o_orderkey FROM orders WHERE orders.o_clerk LIKE '%C' AND orders.o_clerk = 'NZIYR' GROUP BY orders.o_clerk HAVING COUNT(*) > 7814 AND STDDEV(orders.o_orderkey) > 34757 ORDER BY orders.o_clerk