SELECT customer.c_address, customer.c_comment, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, MAX(customer.c_nationkey) AS max_customer_c_nationkey, SUM(customer.c_custkey) AS sum_customer_c_custkey, MIN(customer.c_custkey) AS min_customer_c_custkey, MIN(customer.c_custkey) AS min_customer_c_custkey FROM customer WHERE customer.c_comment LIKE '%B%' AND customer.c_custkey BETWEEN 575 AND 625 AND customer.c_comment LIKE 'A%' GROUP BY customer.c_address, customer.c_comment ORDER BY customer.c_comment, customer.c_address