SELECT region.r_name, region.r_comment, MAX(region.r_regionkey) AS max_region_r_regionkey, STDDEV(region.r_regionkey) AS stddev_region_r_regionkey FROM region WHERE ((region.r_name = 'IHLYN') OR (region.r_comment LIKE '%C')) OR (region.r_name LIKE '%C') GROUP BY region.r_name, region.r_comment HAVING COUNT(*) > 6381 AND MAX(region.r_regionkey) > 92921 ORDER BY region.r_name, region.r_comment