SELECT customer.c_name, customer.c_address, customer.c_comment, STDDEV(customer.c_nationkey) AS stddev_customer_c_nationkey, COUNT(customer.c_nationkey) AS count_customer_c_nationkey, AVG(customer.c_nationkey) AS avg_customer_c_nationkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, MAX(customer.c_custkey) AS max_customer_c_custkey FROM customer WHERE (customer.c_address LIKE 'A%' AND customer.c_address LIKE '%C') OR (customer.c_address = 'SEQMC') GROUP BY customer.c_name, customer.c_address, customer.c_comment HAVING COUNT(*) > 8129 AND VARIANCE(customer.c_nationkey) > 79627 ORDER BY customer.c_name, customer.c_address