SELECT partsupp.ps_comment, VARIANCE(partsupp.ps_availqty) AS variance_partsupp_ps_availqty, VARIANCE(partsupp.ps_supplycost) AS variance_partsupp_ps_supplycost, VARIANCE(partsupp.ps_partkey) AS variance_partsupp_ps_partkey, STDDEV(partsupp.ps_availqty) AS stddev_partsupp_ps_availqty, SUM(partsupp.ps_suppkey) AS sum_partsupp_ps_suppkey FROM partsupp WHERE (partsupp.ps_suppkey = 883) OR (partsupp.ps_comment LIKE '%C') GROUP BY partsupp.ps_comment HAVING COUNT(*) > 1650 AND SUM(partsupp.ps_suppkey) > 76715 ORDER BY partsupp.ps_comment