SELECT orders.o_orderpriority, SUM(orders.o_custkey) AS sum_orders_o_custkey, MAX(orders.o_totalprice) AS max_orders_o_totalprice, MAX(orders.o_totalprice) AS max_orders_o_totalprice, MAX(orders.o_totalprice) AS max_orders_o_totalprice FROM orders WHERE orders.o_orderpriority LIKE '%B%' AND orders.o_orderdate >= '2006-02-26' AND orders.o_custkey BETWEEN 584 AND 951 GROUP BY orders.o_orderpriority HAVING COUNT(*) > 4334 AND MAX(orders.o_totalprice) > 75901 ORDER BY orders.o_orderpriority