SELECT partsupp.ps_comment, COUNT(partsupp.ps_supplycost) AS count_partsupp_ps_supplycost, MIN(partsupp.ps_availqty) AS min_partsupp_ps_availqty, COUNT(partsupp.ps_supplycost) AS count_partsupp_ps_supplycost, VARIANCE(partsupp.ps_availqty) AS variance_partsupp_ps_availqty, MIN(partsupp.ps_availqty) AS min_partsupp_ps_availqty FROM partsupp WHERE partsupp.ps_availqty = 8 GROUP BY partsupp.ps_comment ORDER BY partsupp.ps_comment