SELECT customer.c_phone, MIN(customer.c_custkey) AS min_customer_c_custkey, SUM(customer.c_acctbal) AS sum_customer_c_acctbal, STDDEV(customer.c_nationkey) AS stddev_customer_c_nationkey, STDDEV(customer.c_custkey) AS stddev_customer_c_custkey FROM customer WHERE (customer.c_phone LIKE '%C' AND customer.c_mktsegment = 'TILLH') OR (customer.c_nationkey < 995) GROUP BY customer.c_phone ORDER BY customer.c_phone