SELECT nation.n_name, MAX(nation.n_regionkey) AS max_n_regionkey, COUNT(nation.n_regionkey) AS count_n_regionkey, AVG(nation.n_nationkey) AS avg_n_nationkey FROM nation WHERE nation.n_comment LIKE 'A%' AND nation.n_nationkey = 222 AND nation.n_nationkey < 761 AND nation.n_regionkey = 747 AND nation.n_regionkey > 948 GROUP BY nation.n_name HAVING COUNT(*) > 906 ORDER BY nation.n_name