SELECT nation.n_comment, STDDEV(nation.n_regionkey) AS stddev_nation_n_regionkey, SUM(nation.n_nationkey) AS sum_nation_n_nationkey, MAX(nation.n_nationkey) AS max_nation_n_nationkey FROM nation WHERE ((nation.n_comment LIKE '%B%') OR (nation.n_nationkey = 353)) OR (nation.n_comment LIKE '%B%') GROUP BY nation.n_comment HAVING COUNT(*) > 1672 AND MAX(nation.n_nationkey) > 70520 ORDER BY nation.n_comment