SELECT orders.o_orderpriority, orders.o_clerk, MIN(orders.o_totalprice) AS min_orders_o_totalprice, VARIANCE(orders.o_orderkey) AS variance_orders_o_orderkey, VARIANCE(orders.o_custkey) AS variance_orders_o_custkey, STDDEV(orders.o_orderkey) AS stddev_orders_o_orderkey, VARIANCE(orders.o_totalprice) AS variance_orders_o_totalprice FROM orders WHERE orders.o_orderdate >= '1996-03-25' AND orders.o_orderkey > 539 GROUP BY orders.o_orderpriority, orders.o_clerk ORDER BY orders.o_clerk, orders.o_orderpriority