SELECT supplier.s_name, supplier.s_comment, SUM(supplier.s_acctbal) AS sum_supplier_s_acctbal, MAX(supplier.s_suppkey) AS max_supplier_s_suppkey, SUM(supplier.s_nationkey) AS sum_supplier_s_nationkey, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey, SUM(supplier.s_nationkey) AS sum_supplier_s_nationkey FROM supplier WHERE supplier.s_comment = 'PIWAD' GROUP BY supplier.s_name, supplier.s_comment HAVING COUNT(*) > 2747 AND SUM(supplier.s_nationkey) > 72929 ORDER BY supplier.s_name, supplier.s_comment