SELECT lineitem.l_shipdate, lineitem.l_receiptdate, MIN(lineitem.l_extendedprice) AS min_lineitem_l_extendedprice, MAX(lineitem.l_suppkey) AS max_lineitem_l_suppkey, AVG(lineitem.l_extendedprice) AS avg_lineitem_l_extendedprice, AVG(lineitem.l_extendedprice) AS avg_lineitem_l_extendedprice, MAX(lineitem.l_linenumber) AS max_lineitem_l_linenumber FROM lineitem WHERE (lineitem.l_shipmode LIKE '%B%') OR (lineitem.l_suppkey > 553) AND lineitem.l_shipinstruct LIKE '%C' GROUP BY lineitem.l_shipdate, lineitem.l_receiptdate ORDER BY lineitem.l_shipdate, lineitem.l_receiptdate