SELECT customer.c_mktsegment, customer.c_comment, SUM(customer.c_custkey) AS sum_customer_c_custkey, VARIANCE(customer.c_nationkey) AS variance_customer_c_nationkey, MIN(customer.c_acctbal) AS min_customer_c_acctbal FROM customer WHERE ((customer.c_nationkey > 684) OR (customer.c_address = 'QKBVT') AND customer.c_nationkey = 285) OR (customer.c_nationkey BETWEEN 84 AND 661) GROUP BY customer.c_mktsegment, customer.c_comment ORDER BY customer.c_mktsegment, customer.c_comment