SELECT region.r_name, COUNT(region.r_regionkey) AS count_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey FROM region WHERE region.r_regionkey > 968 AND region.r_comment LIKE '%C' AND region.r_regionkey BETWEEN 206 AND 640 GROUP BY region.r_name HAVING COUNT(*) > 9593 AND COUNT(region.r_regionkey) > 99746 ORDER BY region.r_name