SELECT supplier.s_name, STDDEV(supplier.s_nationkey) AS stddev_supplier_s_nationkey, VARIANCE(supplier.s_acctbal) AS variance_supplier_s_acctbal, MIN(supplier.s_acctbal) AS min_supplier_s_acctbal, MIN(supplier.s_suppkey) AS min_supplier_s_suppkey, COUNT(supplier.s_suppkey) AS count_supplier_s_suppkey FROM supplier WHERE (supplier.s_address LIKE 'A%') OR (supplier.s_phone LIKE '%C') GROUP BY supplier.s_name ORDER BY supplier.s_name