SELECT region.r_comment, VARIANCE(region.r_regionkey) AS variance_region_r_regionkey, AVG(region.r_regionkey) AS avg_region_r_regionkey, COUNT(region.r_regionkey) AS count_region_r_regionkey, MAX(region.r_regionkey) AS max_region_r_regionkey, SUM(region.r_regionkey) AS sum_region_r_regionkey FROM region WHERE ((region.r_comment = 'HXMJU' AND region.r_name = 'OMQFG') OR (region.r_name = 'DZASG')) OR (region.r_comment = 'UZJNZ') GROUP BY region.r_comment HAVING COUNT(*) > 387 AND MAX(region.r_regionkey) > 25162 ORDER BY region.r_comment