SELECT partsupp.ps_comment, STDDEV(partsupp.ps_availqty) AS stddev_partsupp_ps_availqty, COUNT(partsupp.ps_supplycost) AS count_partsupp_ps_supplycost, VARIANCE(partsupp.ps_availqty) AS variance_partsupp_ps_availqty, AVG(partsupp.ps_availqty) AS avg_partsupp_ps_availqty FROM partsupp GROUP BY partsupp.ps_comment HAVING COUNT(*) > 516 AND COUNT(partsupp.ps_supplycost) > 920 ORDER BY partsupp.ps_comment