SELECT customer.c_name, customer.c_phone, COUNT(customer.c_custkey) AS count_customer_c_custkey, COUNT(customer.c_acctbal) AS count_customer_c_acctbal, COUNT(customer.c_custkey) AS count_customer_c_custkey, STDDEV(customer.c_custkey) AS stddev_customer_c_custkey, STDDEV(customer.c_custkey) AS stddev_customer_c_custkey FROM customer WHERE customer.c_name LIKE 'A%' GROUP BY customer.c_name, customer.c_phone ORDER BY customer.c_name, customer.c_phone