SELECT supplier.s_phone, supplier.s_comment, supplier.s_address, MAX(supplier.s_nationkey) AS max_supplier_s_nationkey, AVG(supplier.s_nationkey) AS avg_supplier_s_nationkey, SUM(supplier.s_acctbal) AS sum_supplier_s_acctbal FROM supplier WHERE supplier.s_address LIKE '%C' AND supplier.s_nationkey > 107 GROUP BY supplier.s_phone, supplier.s_comment, supplier.s_address ORDER BY supplier.s_address, supplier.s_comment