SELECT orders.o_orderpriority, orders.o_comment, SUM(orders.o_totalprice) AS sum_orders_o_totalprice, VARIANCE(orders.o_shippriority) AS variance_orders_o_shippriority, SUM(orders.o_totalprice) AS sum_orders_o_totalprice FROM orders WHERE orders.o_clerk = 'YXHUW' AND orders.o_clerk = 'SIQJH' GROUP BY orders.o_orderpriority, orders.o_comment HAVING COUNT(*) > 8079 AND VARIANCE(orders.o_shippriority) > 97188 ORDER BY orders.o_orderpriority, orders.o_comment