SELECT supplier.s_comment, supplier.s_name, AVG(supplier.s_nationkey) AS avg_supplier_s_nationkey, MAX(supplier.s_acctbal) AS max_supplier_s_acctbal FROM supplier WHERE supplier.s_address = 'JFGIA' AND supplier.s_suppkey < 795 AND supplier.s_phone LIKE '%C' GROUP BY supplier.s_comment, supplier.s_name HAVING COUNT(*) > 5173 AND MAX(supplier.s_acctbal) > 7346 ORDER BY supplier.s_comment, supplier.s_name