SELECT L_AIRPORT_SEQ_ID.Description, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID WHERE (L_AIRPORT_SEQ_ID.Code < 10000000 OR L_AIRPORT_SEQ_ID.Code IS NULL) AND L_AIRPORT_SEQ_ID.Code != -999999 GROUP BY L_AIRPORT_SEQ_ID.Description