SELECT L_AIRPORT_SEQ_ID.Code, L_AIRPORT_SEQ_ID.Description, SUM(L_AIRPORT_SEQ_ID.Code) OVER (PARTITION BY L_AIRPORT_SEQ_ID.Description ORDER BY L_AIRPORT_SEQ_ID.Code) AS win_2, ROW_NUMBER() OVER (PARTITION BY L_AIRPORT_SEQ_ID.Description ORDER BY L_AIRPORT_SEQ_ID.Code) AS win_3 FROM L_AIRPORT_SEQ_ID