SELECT On_Time_On_Time_Performance_2016_1.ArrTimeBlk, L_AIRPORT_SEQ_ID.Description, SUM(On_Time_On_Time_Performance_2016_1.Div2LongestGTime) AS sum_On_Time_On_Time_Performance_2016_1_Div2LongestGTime, COUNT(On_Time_On_Time_Performance_2016_1.DepTime) AS count_On_Time_On_Time_Performance_2016_1_DepTime, MIN(On_Time_On_Time_Performance_2016_1.Div1WheelsOn) AS min_On_Time_On_Time_Performance_2016_1_Div1WheelsOn, MIN(On_Time_On_Time_Performance_2016_1.DivAirportLandings) AS min_On_Time_On_Time_Performance_2016_1_DivAirportLandings, SUM(On_Time_On_Time_Performance_2016_1.DivArrDelay) AS sum_On_Time_On_Time_Performance_2016_1_DivArrDelay, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 LEFT JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.UniqueCarrier = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Description IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.ArrTimeBlk, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 85