SELECT On_Time_On_Time_Performance_2016_1.Div2Airport, L_AIRPORT_SEQ_ID.Code, SUM(On_Time_On_Time_Performance_2016_1.OriginStateFips) AS sum_On_Time_On_Time_Performance_2016_1_OriginStateFips, AVG(On_Time_On_Time_Performance_2016_1.Div2TotalGTime) AS avg_On_Time_On_Time_Performance_2016_1_Div2TotalGTime, MIN(On_Time_On_Time_Performance_2016_1.DistanceGroup) AS min_On_Time_On_Time_Performance_2016_1_DistanceGroup, MIN(On_Time_On_Time_Performance_2016_1.FlightNum) AS min_On_Time_On_Time_Performance_2016_1_FlightNum, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.OriginAirportSeqID = L_AIRPORT_SEQ_ID.Code WHERE On_Time_On_Time_Performance_2016_1.WheelsOff > 0 AND L_AIRPORT_SEQ_ID.Code != -999999 AND L_AIRPORT_SEQ_ID.Description LIKE '%' GROUP BY On_Time_On_Time_Performance_2016_1.Div2Airport, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 8