SELECT On_Time_On_Time_Performance_2016_1.DepartureDelayGroups, L_AIRPORT_SEQ_ID.Description, MIN(On_Time_On_Time_Performance_2016_1.TaxiIn) AS min_On_Time_On_Time_Performance_2016_1_TaxiIn, MAX(On_Time_On_Time_Performance_2016_1.AirTime) AS max_On_Time_On_Time_Performance_2016_1_AirTime, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 LEFT JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.OriginCityMarketID = L_AIRPORT_SEQ_ID.Code WHERE On_Time_On_Time_Performance_2016_1.DepTime IS NOT NULL AND L_AIRPORT_SEQ_ID.Description != '' AND L_AIRPORT_SEQ_ID.Description != '' GROUP BY On_Time_On_Time_Performance_2016_1.DepartureDelayGroups, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 6