SELECT On_Time_On_Time_Performance_2016_1.WheelsOn, L_AIRPORT_SEQ_ID.Code, MAX(On_Time_On_Time_Performance_2016_1.Div2WheelsOn) AS max_On_Time_On_Time_Performance_2016_1_Div2WheelsOn, AVG(On_Time_On_Time_Performance_2016_1.DivActualElapsedTime) AS avg_On_Time_On_Time_Performance_2016_1_DivActualElapsedTime, MAX(On_Time_On_Time_Performance_2016_1.ArrDelay) AS max_On_Time_On_Time_Performance_2016_1_ArrDelay, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DestStateFips = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Description IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.WheelsOn, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 9