SELECT On_Time_On_Time_Performance_2016_1.Div1WheelsOn, L_AIRPORT_SEQ_ID.Description, AVG(On_Time_On_Time_Performance_2016_1.Diverted) AS avg_On_Time_On_Time_Performance_2016_1_Diverted, MAX(On_Time_On_Time_Performance_2016_1.Distance) AS max_On_Time_On_Time_Performance_2016_1_Distance, COUNT(On_Time_On_Time_Performance_2016_1.Flights) AS count_On_Time_On_Time_Performance_2016_1_Flights, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.Dest = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Code != -999999 GROUP BY On_Time_On_Time_Performance_2016_1.Div1WheelsOn, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 10