SELECT On_Time_On_Time_Performance_2016_1.OriginCityName, L_AIRPORT_SEQ_ID.Description, STDDEV(On_Time_On_Time_Performance_2016_1.CRSElapsedTime) AS stddev_On_Time_On_Time_Performance_2016_1_CRSElapsedTime, MAX(On_Time_On_Time_Performance_2016_1.Div2LongestGTime) AS max_On_Time_On_Time_Performance_2016_1_Div2LongestGTime, SUM(On_Time_On_Time_Performance_2016_1.Diverted) AS sum_On_Time_On_Time_Performance_2016_1_Diverted, MAX(On_Time_On_Time_Performance_2016_1.Div2TotalGTime) AS max_On_Time_On_Time_Performance_2016_1_Div2TotalGTime, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.Div1AirportID = L_AIRPORT_SEQ_ID.Code WHERE On_Time_On_Time_Performance_2016_1.Div2LongestGTime IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.OriginCityName, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 10