SELECT On_Time_On_Time_Performance_2016_1.ArrDelay, L_AIRPORT_SEQ_ID.Code, AVG(On_Time_On_Time_Performance_2016_1.OriginStateFips) AS avg_On_Time_On_Time_Performance_2016_1_OriginStateFips, MAX(On_Time_On_Time_Performance_2016_1.LongestAddGTime) AS max_On_Time_On_Time_Performance_2016_1_LongestAddGTime, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 LEFT JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DepDel15 = L_AIRPORT_SEQ_ID.Code WHERE (L_AIRPORT_SEQ_ID.Code < 10000000 OR L_AIRPORT_SEQ_ID.Code IS NULL) AND On_Time_On_Time_Performance_2016_1.OriginCityName != '' AND On_Time_On_Time_Performance_2016_1.CarrierDelay IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.ArrDelay, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 10