SELECT On_Time_On_Time_Performance_2016_1.TotalAddGTime, L_AIRPORT_SEQ_ID.Description, MAX(On_Time_On_Time_Performance_2016_1.OriginWac) AS max_On_Time_On_Time_Performance_2016_1_OriginWac, SUM(On_Time_On_Time_Performance_2016_1.OriginAirportSeqID) AS sum_On_Time_On_Time_Performance_2016_1_OriginAirportSeqID, MAX(On_Time_On_Time_Performance_2016_1.Div1AirportSeqID) AS max_On_Time_On_Time_Performance_2016_1_Div1AirportSeqID, MIN(On_Time_On_Time_Performance_2016_1.ArrDelayMinutes) AS min_On_Time_On_Time_Performance_2016_1_ArrDelayMinutes, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.Quarter = L_AIRPORT_SEQ_ID.Code WHERE On_Time_On_Time_Performance_2016_1.Div1TailNum != '' GROUP BY On_Time_On_Time_Performance_2016_1.TotalAddGTime, L_AIRPORT_SEQ_ID.Description