SELECT On_Time_On_Time_Performance_2016_1.Dest, STDDEV(On_Time_On_Time_Performance_2016_1.DivAirportLandings) AS stddev_On_Time_On_Time_Performance_2016_1_DivAirportLandings, MAX(On_Time_On_Time_Performance_2016_1.Div1LongestGTime) AS max_On_Time_On_Time_Performance_2016_1_Div1LongestGTime, STDDEV(On_Time_On_Time_Performance_2016_1.LateAircraftDelay) AS stddev_On_Time_On_Time_Performance_2016_1_LateAircraftDelay, COUNT(On_Time_On_Time_Performance_2016_1.CRSArrTime) AS count_On_Time_On_Time_Performance_2016_1_CRSArrTime, AVG(On_Time_On_Time_Performance_2016_1.Div2AirportSeqID) AS avg_On_Time_On_Time_Performance_2016_1_Div2AirportSeqID, SUM(On_Time_On_Time_Performance_2016_1.TaxiIn) AS sum_On_Time_On_Time_Performance_2016_1_TaxiIn FROM On_Time_On_Time_Performance_2016_1 WHERE On_Time_On_Time_Performance_2016_1.DestStateName != '' AND On_Time_On_Time_Performance_2016_1.WheelsOff > 0 GROUP BY On_Time_On_Time_Performance_2016_1.Dest