SELECT On_Time_On_Time_Performance_2016_1.DepDelayMinutes, L_AIRPORT_SEQ_ID.Code, MAX(On_Time_On_Time_Performance_2016_1.DivReachedDest) AS max_On_Time_On_Time_Performance_2016_1_DivReachedDest, MIN(On_Time_On_Time_Performance_2016_1.Div1WheelsOn) AS min_On_Time_On_Time_Performance_2016_1_Div1WheelsOn, SUM(On_Time_On_Time_Performance_2016_1.ArrivalDelayGroups) AS sum_On_Time_On_Time_Performance_2016_1_ArrivalDelayGroups, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID INNER JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Dest WHERE L_AIRPORT_SEQ_ID.Description IS NOT NULL AND On_Time_On_Time_Performance_2016_1.DivReachedDest IS NOT NULL AND L_AIRPORT_SEQ_ID.Description IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.DepDelayMinutes, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 1