SELECT On_Time_On_Time_Performance_2016_1.DayOfWeek, L_AIRPORT_SEQ_ID.Code, COUNT(On_Time_On_Time_Performance_2016_1.DivArrDelay) AS count_On_Time_On_Time_Performance_2016_1_DivArrDelay, AVG(On_Time_On_Time_Performance_2016_1.TaxiIn) AS avg_On_Time_On_Time_Performance_2016_1_TaxiIn, COUNT(On_Time_On_Time_Performance_2016_1.WheelsOn) AS count_On_Time_On_Time_Performance_2016_1_WheelsOn, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DestState = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Code IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.DayOfWeek, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 3