SELECT On_Time_On_Time_Performance_2016_1.ArrDelay, L_AIRPORT_SEQ_ID.Description, AVG(On_Time_On_Time_Performance_2016_1.DestAirportSeqID) AS avg_On_Time_On_Time_Performance_2016_1_DestAirportSeqID, SUM(On_Time_On_Time_Performance_2016_1.FirstDepTime) AS sum_On_Time_On_Time_Performance_2016_1_FirstDepTime, COUNT(On_Time_On_Time_Performance_2016_1.DestCityMarketID) AS count_On_Time_On_Time_Performance_2016_1_DestCityMarketID, AVG(On_Time_On_Time_Performance_2016_1.ArrivalDelayGroups) AS avg_On_Time_On_Time_Performance_2016_1_ArrivalDelayGroups, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DistanceGroup = L_AIRPORT_SEQ_ID.Code WHERE On_Time_On_Time_Performance_2016_1.FirstDepTime IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.ArrDelay, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 5