SELECT On_Time_On_Time_Performance_2016_1.DestStateName, On_Time_On_Time_Performance_2016_1.OriginCityName, L_AIRPORT_SEQ_ID.Description, MIN(On_Time_On_Time_Performance_2016_1.Distance) AS min_On_Time_On_Time_Performance_2016_1_Distance, STDDEV(On_Time_On_Time_Performance_2016_1.DestWac) AS stddev_On_Time_On_Time_Performance_2016_1_DestWac, COUNT(On_Time_On_Time_Performance_2016_1.Div1AirportSeqID) AS count_On_Time_On_Time_Performance_2016_1_Div1AirportSeqID, MAX(On_Time_On_Time_Performance_2016_1.ArrDel15) AS max_On_Time_On_Time_Performance_2016_1_ArrDel15, SUM(On_Time_On_Time_Performance_2016_1.OriginCityMarketID) AS sum_On_Time_On_Time_Performance_2016_1_OriginCityMarketID, COUNT(On_Time_On_Time_Performance_2016_1.FirstDepTime) AS count_On_Time_On_Time_Performance_2016_1_FirstDepTime, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.OriginAirportSeqID = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Description IS NOT NULL AND On_Time_On_Time_Performance_2016_1.CRSDepTime IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.DestStateName, On_Time_On_Time_Performance_2016_1.OriginCityName, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 85