SELECT On_Time_On_Time_Performance_2016_1.Div2LongestGTime, L_AIRPORT_SEQ_ID.Description, MIN(On_Time_On_Time_Performance_2016_1.DayofMonth) AS min_On_Time_On_Time_Performance_2016_1_DayofMonth, COUNT(On_Time_On_Time_Performance_2016_1.TaxiOut) AS count_On_Time_On_Time_Performance_2016_1_TaxiOut, COUNT(On_Time_On_Time_Performance_2016_1.LongestAddGTime) AS count_On_Time_On_Time_Performance_2016_1_LongestAddGTime, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Month WHERE L_AIRPORT_SEQ_ID.Code IS NOT NULL AND LENGTH(L_AIRPORT_SEQ_ID.Description) > 0 GROUP BY On_Time_On_Time_Performance_2016_1.Div2LongestGTime, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 2