SELECT On_Time_On_Time_Performance_2016_1.Origin, L_AIRPORT_SEQ_ID.Description, STDDEV(On_Time_On_Time_Performance_2016_1.DivArrDelay) AS stddev_On_Time_On_Time_Performance_2016_1_DivArrDelay, MIN(On_Time_On_Time_Performance_2016_1.DayOfWeek) AS min_On_Time_On_Time_Performance_2016_1_DayOfWeek, MAX(On_Time_On_Time_Performance_2016_1.FirstDepTime) AS max_On_Time_On_Time_Performance_2016_1_FirstDepTime, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DistanceGroup = L_AIRPORT_SEQ_ID.Code GROUP BY On_Time_On_Time_Performance_2016_1.Origin, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 37