SELECT On_Time_On_Time_Performance_2016_1.Dest, L_AIRPORT_SEQ_ID.Description, COUNT(On_Time_On_Time_Performance_2016_1.Div2AirportID) AS count_On_Time_On_Time_Performance_2016_1_Div2AirportID, COUNT(On_Time_On_Time_Performance_2016_1.LateAircraftDelay) AS count_On_Time_On_Time_Performance_2016_1_LateAircraftDelay, MAX(On_Time_On_Time_Performance_2016_1.DepDel15) AS max_On_Time_On_Time_Performance_2016_1_DepDel15, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID INNER JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.OriginState WHERE (L_AIRPORT_SEQ_ID.Code < 10000000 OR L_AIRPORT_SEQ_ID.Code IS NULL) AND L_AIRPORT_SEQ_ID.Code > 0 AND LENGTH(On_Time_On_Time_Performance_2016_1.DestCityName) > 0 GROUP BY On_Time_On_Time_Performance_2016_1.Dest, L_AIRPORT_SEQ_ID.Description