SELECT On_Time_On_Time_Performance_2016_1.ArrTimeBlk, L_AIRPORT_SEQ_ID.Code, COUNT(On_Time_On_Time_Performance_2016_1.CRSElapsedTime) AS count_On_Time_On_Time_Performance_2016_1_CRSElapsedTime, MIN(On_Time_On_Time_Performance_2016_1.DepDelay) AS min_On_Time_On_Time_Performance_2016_1_DepDelay, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 LEFT JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DestAirportID = L_AIRPORT_SEQ_ID.Code WHERE (L_AIRPORT_SEQ_ID.Code < 10000000 OR L_AIRPORT_SEQ_ID.Code IS NULL) AND On_Time_On_Time_Performance_2016_1.DayOfWeek IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.ArrTimeBlk, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 2